import React, { Component } from 'react';
class Card extends Component {

state={selected:false,focused:false};

onClick=(e)=>{
  e.preventDefault();
this.setState({ selected:!this.state.selected})
 } 

onPointer=()=>{
  alert("123");
}
onFocus=()=>{
this.setState({focused:true});
}
onLeave=()=>{
this.setState({focused:false});
}

  render() {
const opacity=this.state.focused?"0.3":"0";
  	const {active,taste,quantity,gift,description,weight}=this.props.itemData;
  const color=active?(!this.state.selected?"rgba(22,152,217,"+opacity+")":"rgba(217,22,103,"+opacity+")"):"#b3b3b3";

    const weightStyle={
      "background-color" : !this.state.selected?("#1698d9"):("#d91667")
    }



    return <div className="full_card"> 
    <div className="card" onClick={this.onClick} onPointerOver={this.onFocus} onPointerLeave={this.onLeave} >
    <svg>
<polyline points="0,45 40,2 318,2 318,478 2,478 2,45 " fill={active?"none":"rgba(242,242,242,0.8)"} stroke-width="4" stroke={color}/>
  </svg>
   <p className="card_first card_item">Сказачное заморское яство</p>
   <h1 className="card_title card_item">Нямушка</h1>
   <p className="card_taste card_item">{taste}</p>
   <p className="card_quantity card_item">{quantity}</p>
   <p className="card_gift card_item">{gift}</p>
   <div className="card_weight" style={weightStyle}>
   <span className="weight_value" >
   {weight}</span> 
   <br/>
   <span className="kilos">
   кг
  </span>
   </div>
   </div>
<div className="card_description">
{!this.state.selected?<p>Чего сидишь? Порадуй котэ, <a onClick={this.onClick}>купи</a></p>:<p>{description}</p>} 
</div>
   </div>
   ;

}
}

export default Card;
